import React, { useState, useEffect } from "react";
import { Input, Tag, Row, Button } from "antd";
import SpotifyWebApi from "spotify-web-api-js";

const spotifyApi = new SpotifyWebApi();
const { Search } = Input;

function Playlist() {
  const [tags, setTags] = useState([]);
  const colors = [
    "magenta",
    "red",
    "volcano",
    "orange",
    "gold",
    "lime",
    "green",
    "cyan",
    "blue",
    "geekblue",
    "purple",
  ];

  useEffect(() => {
    console.log("tags: ", tags);
  });

  function addTag(e) {
    if (tags.includes(e.target.value)) {
      alert("Add a different word!");
    } else {
      setTags(tags.concat(e.target.value));
    }
  }

  function removeTag(e) {
    let tagRemoved =
      e.target.parentNode.parentNode.textContent ||
      e.target.parentNode.parentNode.parentNode.textContent;
    setTags(tags.filter((e) => e !== tagRemoved));
  }

  function renderTags() {
    let tags_arr = [];
    for (let i = 0; i < tags.length; i++) {
      let color = colors[Math.floor(Math.random() * colors.length)];
      tags_arr.push(
        <Tag color={color} closable onClose={removeTag}>
          {tags[i]}
        </Tag>
      );
    }
    return tags_arr;
  }

  return (
    <div style={{ height: "100vh" }}>
      <Row
        type="flex"
        justify="center"
        align="bottom"
        style={{ height: "40%" }}
      >
        <Input
          allowClear
          size="large"
          placeholder="Input five words"
          onPressEnter={addTag}
          style={{ width: "50%" }}
        />
      </Row>
      <br />
      <Row type="flex" justify="center" align="top" style={{ height: "40%" }}>
        {renderTags()}
      </Row>
      <br />
      <Row type="flex" justify="top" align="top" style={{ height: "20%" }}>
        <Button type="primary">Generate Cover Art!</Button>
      </Row>
    </div>
  );
}

export default Playlist;
